import React, { useState } from "react";
import Footer from "./components/Footer";
import Header from "./components/Header";
import Items from "./components/Items";

function App() {
  const data = [
    {id: 1,
    title:'Стул сeрый',
    img: './img/chair.jpg',
    desc: 'Lorem ipsum dolor sit amet, consectetur adipisicing',
    category: 'chairs',
    price: '49.99'
    },
    {id: 2,
      title:'Стол',
      img: '',
      desc: 'Lorem ipsum dolor sit amet, consectetur adipisicing',
      category: 'tables',
      price: '149.00'
    },
    {id: 3,
      title:'Диван',
      img: '',
      desc: 'Lorem ipsum dolor sit amet, consectetur adipisicing',
      category: 'sofa',
      price: '549.99'
    },
    {id: 4,
      title:'Лампа',
      img: '',
      desc: 'Lorem ipsum dolor sit amet, consectetur adipisicing',
      category: 'light',
      price: '25.00'
    }
  ]
  const [item, setItem] = useState(data)
  
  return (
   <div className="wrapper">
    <Header/>
    
    <Items items={item}/>
    <Footer/>
   </div>
  );
}

export default App;
