import React from 'react'

export default function Item(props) {
  let data = props.data
  return (
    <div className={'item'}>
      <img src={"./img/" + data.img} alt=''/>
      <h2>{data.title}</h2>
      <p>{data.desc}</p>
      <b>{data.price}$</b>
      <div className='add-to-cart' onClick={() => props.onAdd(data)}>+</div>
    </div>
  )
}
